const orderProjectBlocks=()=>{const o=$(".project__image");for(let e=1;e<o.length;e+=2){$(o[e]).css("order","1")}},scrollToProjects=()=>{document.getElementById("projects").scrollIntoView({behavior:"smooth"})};function main(){orderProjectBlocks(),$("#scrollDown").click(scrollToProjects),new Glide(".glide",{autoplay:3e3,hoverpause:!0}).mount()}$(document).ready(main);